-- ACTUALIZACIÓN PREGUNTAS ESCENARIO 102 (escenario_id = 57)
-- Protocolo SNAP para intoxicación por paracetamol
-- 36 preguntas: 14 médico + 12 enfermería + 10 farmacia
-- Briefing: Niña 8 años, 26 kg, 8 × 500 mg paracetamol hace 3h (153.8 mg/kg)
-- 4 críticas (1 por step), solo la crítica con temporizador >90 por step

DELETE FROM attempt_answers WHERE question_id IN (SELECT q.id FROM questions q JOIN steps s ON s.id = q.step_id WHERE s.scenario_id = 57);
DELETE FROM questions WHERE step_id IN (SELECT id FROM steps WHERE scenario_id = 57);

